commits.content:
.sidebar{
overflow: hidden;
padding: 0 2rem;

}

.rightSide{
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 6rem;

}
.rightSide > div:first-child h1{
  font-size: 4rem;
}
.rightSide h2{
  font-size: 2rem;
  color: var(--secondary-blue);
}
.rightSide h3{
  font-size: 1.4rem;
  color: var(--tertiary-green);
}

.rightSide div:nth-of-type(2){
display:flex;
flex-direction: column;
gap: 1rem;
}

.rightSide div:nth-of-type(2) > h4:first-child{
color: var(--primary-dark);
font-size: 1.4rem;
}

.rightSide div:nth-of-type(2) > h4:nth-of-type(2) span:nth-of-type(1){
color: var(--secondary-blue);
}

.rightSide div:nth-of-type(2) > h4:nth-of-type(2) span:nth-of-type(2){
  color: var(--tertiary-green);
}

.rightSide div:nth-of-type(2) > h4:nth-of-type(2) span:nth-of-type(3){
color: var(--tertiary-orange);
}

.rightSide div:nth-of-type(2) >span>a{
color: var(--tertiary-orange);
text-decoration:none;
}

.rightSide div:nth-of-type(2) >span>a>span{
  color: var(--tertiary-green);
  }

  .rightSide div:first-child > h1 >span{
  display: inline-block;
}

.sidebar > img {
  border-bottom: 0.04rem solid var(--primary-dark);
border-radius: 0.6rem;
float: right;

}


.sidebar>img{
  display:inline-block;
  transform: scale(0.8);
}

.sidebar>img:nth-of-type(2){
  display:inline-block;
  transform: scale(1);
}

.rightSide div > h2 > span {
  display: inline-block;
  animation: arrow 2s infinite alternate;
}

@keyframes arrow {
  from{
    transform: translateX(-0.6rem);
  }
}

 .rightSide > div:nth-child(2)>span>span {
  animation: underscore 0.2s infinite alternate;
}

@keyframes underscore {
  from {
    opacity: 0;
  }
  to{
    opacity: 1;
  }
}

.underscore{
  color: var(--tertiary-orange);
}
