commits.content:
.projects-left-side{
  border: 1px solid red;
  display: none;
}
.projects-right-side{
  border: 1px solid red;
  grid-column: 1/3;
  display: flex;
  justify-content: space-between;
  padding:1rem ;
}

button .project-item{
  border: 1px solid pink;
width: 20vw;
height: 20vw;
box-sizing: border-box;
position: relative;
}

.project-item:hover{
cursor: pointer;
box-shadow: 0.2rem 0.2rem 2rem 0 var(--secondary-blue);
}

.project-item > h2{
position: absolute;
bottom: 50%;
text-align: center;
width: 100%;

}
